import React from 'react'
//import User from './User.jsx'

class Users extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			users: [
				{
					id: 1,
					firstname: 'Bob',
					lastname: 'Marley',
					bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Autem, unde eaque illum architecto esse odio accusantium neque a nobis reprehenderit veniam est, earum.',
					age: 40,
					isHappy: true
				},
				{
					id: 2,
					firstname: 'John',
					lastname: 'Doe',
					bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim animi dolorem, aliquid, sunt ullam repellat?',
					age: 22,
					isHappy: false
				}
			]
		}
	}
	render() {
		if (this.state.users.length > 0)
			return (
				<div>{this.state.users.map((el) => (
					<div className="user" key={el.id}>
						<h3>{el.firstname} {el.lastname}</h3>
						<p>{el.bio}</p>
						<b>{el.isHappy ? 'Счастлив :)' : 'Не особо :('}</b>
					</div>))}
				</div>
			)
		else
			return (
				<div className="user">
					<h3>Пользователей нет</h3>
				</div>
			)
	}
}
export default Users;
