import React from 'react'
import Header from './components/Header'
import Users from './components/Users'
import AddUser from './components/AddUser'

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			users: [
				{
					id: 1,
					firstname: 'Bob',
					lastname: 'Marley',
					bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Autem, unde eaque illum architecto esse odio accusantium neque a nobis reprehenderit veniam est, earum.',
					age: 40,
					isHappy: true
				},
				{
					id: 2,
					firstname: 'John',
					lastname: 'Doe',
					bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim animi dolorem, aliquid, sunt ullam repellat?',
					age: 22,
					isHappy: false
				}
			]
		}
	}
	render() {
		return (
			<div>
				<Header title="Список пользователей" />
				<main>
					<Users users={this.state.users} />
				</main>
				<aside>
					<AddUser />
				</aside>
			</div>
		)
	}
}

export default App
