import React, {useState} from 'react'
import Header from './components/Header'
import Users from './components/Users'
import AddUser from './components/AddUser'
import card from './data'

function App() {
	const [card, setCard] = useState(card);
	const deleteUser = (id) => {
		setCard((card) => card.filter((el) => el.id !== id));
	}
	/*const deleteUser = (id) => {
		setCard((card) => {
			card.forEach((el, index) => {
				if (id == el.id) {
					card.splice(index, 1);
				}
			});
		});
	}*/

	const [edit, setEdit] = useState(card);
	const editUser = (id) => {
		setEdit((edit) => {
			return edit.map((user) => {
				let allUsers = users
				allUsers[user.id - 1] = user
				setEdit((edit) => {
					edit = [];
					edit = [...allUsers];
				});
			})
		})
	}
	const [add, setAdd] = useState({card});
	const addUser = (id) => {
		setAdd((edit) => {
			const id = card.length + 1
			return add.map((user) => {
				setAdd((edit) => {
					card = [...this.state.card, {id, ...user}];
				});
			})
		})
	}
	return (
		<div>
			<Header title="Список пользователей" />
			<main>
				<Users card={users} onEdit={editUser} onDelete={deleteUser} />
			</main>
			<aside>
				<AddUser onAdd={addUser} />
			</aside>
		</div>
	);
}

export default App
